class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        d = dict()
        for i in range(0, n//2):
            dis = abs(nums[i] - nums[n-1-i])
            if dis in d:
                d[dis] += 1
            else:
                d[dis] = 1
        x, m = 0, 0
        sv = set()
        mv = dict()
        for key, value in d.items():
            sv.add(value)
            if value in mv:
                if mv[value] > key:
                    mv[value] = key
            else:
                mv[value] = key
        res = n+1
        sv = sorted(list(sv), reverse=True)
        for v in sv:
            x = mv[v]
            r = 0
            for i in range(0, n//2):
                if abs(nums[i] - nums[n-1-i]) == x:
                    continue
                if nums[i]-x<0 and nums[i]+x>k and nums[n-1-i]-x<0 and nums[n-1-i]+x>k:
                    r+=2
                else:
                    r+=1
            # print(x, r)
            if r > res:
                break
            res = r
        return res
