class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mp;
        vector<int>pref(nums.size()/2);
        for(int i=0;i<nums.size()/2;i++){
            int diff = abs(nums[i]-nums[nums.size()-1-i]);
            int minCheck = k-nums[i];
            int maxCheck = k-nums[nums.size()-1-i];
            mp[diff].push_back(max(minCheck,max(maxCheck,max(nums[i],nums[nums.size()-1-i]))));
            pref[i] = max(minCheck,max(maxCheck,max(nums[i],nums[nums.size()-1-i])));
        }
        sort(pref.begin(),pref.end());
        int counter = nums.size();
        for(auto it = mp.begin(); it!=mp.end();it++){
            int diff = it->first;
            int min1 = lower_bound(pref.begin(),pref.end(),diff)-pref.begin();
            int temp = min1*2;
            temp += nums.size()/2-min1;
            for(int i=0;i<it->second.size();i++){
                if(it->second[i]<diff){
                    temp-=2;
                }else{
                    temp-=1;
                }
            }
            if(counter>temp){
                counter = temp;
            }
        }
        return counter;
    }
};
