const int N = 100010;
class Solution {
int cnt[N];
// 两个数改一个达不成这个效果
int g[N];
public:
    int minChanges(vector<int>& a, int k) {
        // n / 2数对限制
        // 整数X一定
        int n = a.size();
        for (int i = 0;i <= n / 2 - 1;i ++) {
            int o = a[n - i - 1];
            int c = a[i];
            int r = abs(o - c);
            cnt[r] ++;
            // 这个数对改多少需要两次
            // 验证一下限制，用前缀和处理
            int ans = 0;
            if (o < c) {
                ans = max(c,k - o);
            }else {
                ans = max(o,k - c);
            }
            g[ans] ++;
        }
        for (int i = 0;i <= k;i ++) {
            if (i) g[i] += g[i - 1];  
        }
        // for (int i = 0;i <= k;i ++) {
        //     cout << g[i] << endl;
        // }

        // 找数对的次数，然后判断需要额外几次，然后取最小值
        int res = 0x3f3f3f3f;
        for (int i = 0;i <= k;i ++) {
            // 遍历所有可能的差值
            int tmp = cnt[i];
            // 如果没有出现过，直接continue
            int x = n / 2 - tmp; // 所有没出现的数对数目
           res = min (res,x + g[i - 1]);
        }
        return res;
    }
};
