class Solution {
public:
    int minChanges(vector<int>& arr, int limit) {
        auto differencesMap = computeDifferencesMap(arr, limit);
        auto maxValues = extractMaxValues(arr, limit);
        sort(maxValues.begin(), maxValues.end());
        return calculateMinChanges(arr, differencesMap, maxValues);
    }

private:
    map<int, vector<int>> computeDifferencesMap(const vector<int>& arr, int limit) {
        map<int, vector<int>> differencesMap;
        int size = arr.size();
        for (int i = 0; i < size / 2; ++i) {
            int start = arr[i];
            int end = arr[size - i - 1];
            int diff = abs(start - end);
            int maxVal = max({start, end, limit - start, limit - end});
            differencesMap[diff].push_back(maxVal);
        }
        return differencesMap;
    }

    vector<int> extractMaxValues(const vector<int>& arr, int limit) {
        vector<int> maxValues;
        int size = arr.size();
        for (int i = 0; i < size / 2; ++i) {
            int start = arr[i];
            int end = arr[size - i - 1];
            int maxVal = max({start, end, limit - start, limit - end});
            maxValues.push_back(maxVal);
        }
        return maxValues;
    }

    int calculateMinChanges(const vector<int>& arr, const map<int, vector<int>>& differencesMap, const vector<int>& maxValues) {
        int size = arr.size();
        int minChanges = size;
        for (const auto& [difference, values] : differencesMap) {
            int position = lower_bound(maxValues.begin(), maxValues.end(), difference) - maxValues.begin();
            int changes = position * 2 + (size / 2 - position);
            for (const auto& maxVal : values) {
                if (maxVal < difference) {
                    changes -= 2;
                } else {
                    changes -= 1;
                }
            }
            minChanges = min(minChanges, changes);
        }
        return minChanges;
    }
};
