class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        map<int,int> m;
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            int diff=abs(nums[i]-nums[n-i-1]);

            m[diff]++;
        }

        int diff=0;
        int maxval=0;
        for(auto i:m){

            if(i.second>maxval){
                maxval=i.second;
                diff=i.first;
            }
        }

        // cout<<diff<<endl;

        if(n<1000){

            int ans=1e9;
            for(auto i:m){
                int d=i.first;
                int ret=0;
                for(int i=0;i<n/2;i++){

                    if(abs(nums[i]-nums[n-i-1])==d){
                        continue;
                    }
                    else{
                        int a=nums[i];
                        int b=nums[n-i-1];
                        if(a>b)swap(a,b);

                        if(b-d>=0)ret++;
                        else if(a+d<=k)ret++;
                        else ret+=2;
                    }
                }

                // cout<<d<<" "<<ret<<endl;
                ans=min(ans,ret);

            }


            return ans;

        }
        int ret=0;
        for(int i=0;i<n/2;i++){

            if(abs(nums[i]-nums[n-i-1])==diff){
                continue;
            }
            else{
                int a=nums[i];
                int b=nums[n-i-1];
                if(a>b)swap(a,b);

                if(b-diff>=0)ret++;
                else if(a+diff<=k)ret++;
                else ret+=2;
            }
        }

        return ret;

    }
};
