		boyDiff := int(math.Abs(float64(boyA - boyB)))
		boyX := max(boyA, boyB, k-boyA, k-boyB)
		boyMap[boyDiff] = append(boyMap[boyDiff], boyX)
		boyVector = append(boyVector, boyX)
	}

	boyAns := len(nums)
	sort.Ints(boyVector)

