func max(values ...int) int {
	boyMax := values[0]
	for _, val := range values {
		if val > boyMax {
			boyMax = val
		}
	}
	return boyMax
}

func lowerBound(array []int, value int) int {
	boyLeft, boyRight := 0, len(array)
	for boyLeft < boyRight {
		boyMid := (boyLeft + boyRight) / 2
		if array[boyMid] < value {
			boyLeft = boyMid + 1
		} else {
			boyRight = boyMid
		}
	}
	return boyLeft
}
