class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        # 计算差值的众数
        g = defaultdict(int)
        n = len(nums)
        for i in range(n // 2):
            g[abs(nums[i] - nums[n - 1 - i])] += 1
        x = []
        mx = max(g.values())
        for kk in g.keys():
            if g[kk] == mx:
                x.append(kk)
        # print(x, "111", g.values())
        # print(x)
        xx = []
        cmx = 0 
        for y in g.values():
            if y != mx:
                cmx = max(cmx, y)
        for kk, v in g.items():
            if v == cmx:
                xx.append(kk)
        def calc(x: int):
            ans = 0
            for i in range(n // 2):
                if abs(nums[i] - nums[n - 1 - i]) == x:
                    continue 
                a, b = nums[i], nums[n - 1 - i]
                # y - a < 0 a - y = x 
                # y - a > 0 y - a = x 
                if 0 <= a - x <= k or 0 <= a + x <= k or 0 <= b - x <= k or 0 <= b + x <= k:
                    # print(a, b, "999")
                    ans += 1
                    continue 
                ans += 2
                # print(a, b)
            return ans 
        res = 0 
        res = min(calc(y) for y in x)
        if xx:
            res = min(min(calc(y) for y in xx), res)
        return res
