class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> pref(100005 , 0);
        int st = 0  , end = nums.size() - 1;
        while(st < end)
        {
            int mn = min(nums[st] , nums[end]);
            int mx = max(nums[st] , nums[end]);
            pref[0] += 1;
            pref[mx - mn] -= 1;
            pref[mx - mn + 1] += 1;
            int p = max(mx , k - mn);
            pref[p + 1] -= 1;
            pref[p + 1] += 2;
            pref[k + 1] -= 2;
            st++;
            end--;
        }
        for(int i = 1 ; i <= k ; i++)
        {
            pref[i] += pref[i - 1];
        }
        int ans = 1e9;
        for(int i = 0 ; i <= k ; i++)
        {
            ans = min(ans , pref[i]);
        }
        return ans;
    }
};
