class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> diff((int)1e5+1, 0);
        vector<int> count1((int)1e5+1, 0);
        int n = nums.size();
        for(int i = 0; i < n / 2; i++) {
            int cur_diff = abs(nums[i] - nums[n - i - 1]);
            diff[cur_diff]++;
            int l1, r1, l2, r2;
            l1 = max(0, nums[i] - k);
            r1 = max(nums[i], k - nums[i]);
            l2 = max(0, nums[n - i - 1] - k);
            r2 = max(nums[n - i - 1], k - nums[n - i - 1]);
            if (r1 < l2 || r2 < l1) {
                count1[l1]++;
                count1[l2]++;
                count1[r1+1]--;
                count1[r2+1]--;
                if (cur_diff >= l1 && cur_diff <= r1 || cur_diff >= l2 &&cur_diff <= r2) {
                    count1[cur_diff]--;
                    count1[cur_diff+1]++;
                }
            }else {
                int l = min(l1, l2);
                int r = max(r1, r2);
                count1[l]++;
                count1[r+1]--;
                if (cur_diff >= l && cur_diff <= r) {
                    count1[cur_diff]--;
                    count1[cur_diff+1]++;
                }
            }
        }
        int res = INT_MAX, pref = 0;
        for(int x = 0; x < (int) 1e5 + 1; x++) {
            pref += count1[x];
            res = min(res, pref + (n / 2 - pref - diff[x]) * 2);
        }
        return res;

    }
};
