class Solution {
public:
    int f(vector<int> n,int k,int x){
        int ans=0;

        for(int i=0;i<n.size()/2;i++){
            int t=abs(n[i]-n[n.size()-1-i]);
            if(t!=x){
                if(n[i]+4<=k || n[i]-4>=0 || n[n.size()-1-i]+4<=k ||  n[n.size()-1-i]-4>=0){
                    ans++;
                }
                else{
                    ans+=2;
                }
            }
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        vector<int> vlll;
        int ans = nums.size();  
        map<int, vector<int>> mp;
        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

            int X = max({a, b, k - a, k - b});
            mp[diff].push_back(X);
            vlll.push_back(X);
        }
        sort(vlll.begin(), vlll.end());



        for (auto [d, v] : mp) {
            int temp = lower_bound(vlll.begin(), vlll.end(), d) - vlll.begin();
            temp = temp * 2 + (nums.size() / 2 - temp);

            for (auto X : v) {
                if (X < d) {
                    temp -= 2;
                } else {
                    temp -= 1;
                }
                ans = min(ans, temp);
            }


        }

        return ans;


    }
};
