class Solution {
public:
    bool inRange(int k,int some){
        return some>=0&&some<=k;
    }
    int getcount(vector<int>& nums,int x,int k,int n){
        int ct=0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-i-1])!=x){
                if(inRange(k,nums[i]+x)||inRange(k,nums[i]-x)||inRange(k,nums[n-i-1]+x)||inRange(k,nums[n-i-1]-x))
                {
                    ct++;
                }
                else{
                    ct+=2;
                }
            }
        }
        return ct;
    }
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp,fq;
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
        }
        for(auto &val:mp){
            if(fq.find(val.second)==fq.end()){
                fq[val.second]=val.first;
            }
        }
        int ct=INT_MAX;
        for(auto &val:fq){
            ct=min(ct,getcount(nums,val.second,k,n));
        }
        return ct;
    }
};
