        for(int i=0;i<v.size();i++){
            m[v[i]]++;
            if(m[v[i]]>maxi){
                maxi=m[v[i]];
                ele=v[i];
            }
        }
        int nn=v.size();
        int med;
        if(nn&1){
            med=v[nn/2];
            i=0;
        j=n-1;
