                i++;
                j--;
            }
            else{
                int mx1=max(k-a[i],a[i]);
                int mx2=max(k-a[j],a[j]);
                if(med<=mx1 || med<=mx2){
                    ans++;
                }
                else ans+=2;
                i++;
                j--;
            }
        }
        }
        else{
            // med=v[nn/2]+v[nn/2  -1];
            // med/=2;
            med=v[nn/2];
            i=0;
            j=n-1;
            while(i<=j){
                if(abs(a[i]-a[j])==med){
                    i++;
                    j--;
                }
                else{
                    int mx1=max(k-a[i],a[i]);
                    int mx2=max(k-a[j],a[j]);
                    if(med<=mx1 || med<=mx2){
                        ans++;
                    }
                    else ans+=2;
                    i++;
                    j--;
                }
            }
            med=v[nn/2  -1];
            int ans2=0;
            i=0;
            j=n-1;
            while(i<=j){
                if(abs(a[i]-a[j])==med){
                    i++;
                    j--;
                }
                else{
                    int mx1=max(k-a[i],a[i]);
                    int mx2=max(k-a[j],a[j]);
                    if(med<=mx1 || med<=mx2){
                        ans2++;
                    }
                    else ans2+=2;
                    i++;
                    j--;
                }
            }
            ans=min(ans,ans2);
        }
        int ans3=0;

         i=0;
