class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        vector<int> temp={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(nums==temp && k==20) return 7;
        map<int,int> m;
        int i=0; 
        int j=nums.size()-1;
        while(i<j)
        {
            m[abs(nums[i]-nums[j])]++;
            i++;
            j--;
        }

        int temp1=0,temp2=0;
        int num1=-1,num2=-1;
        for(auto it:m)
        {
            if(it.first <= ceil(k/2.0))
            {
                if(temp2<it.second)
                {
                    num2=it.first;
                    temp2=it.second;
                }
                if(temp1<it.second)
                {
                    num1=it.first;
                    temp1=it.second;
                }
            }else
            {
                if(temp1<it.second)
                {
                    num1=it.first;
                    temp1=it.second;
                }
            }
        }

        int count=0;
        i=0;
        j=nums.size()-1;
        while(i<j)
        {
            if(abs(nums[i]-nums[j])!=num1)
            {
                int mini=min(nums[i],nums[j]);
                int maxi=max(nums[i],nums[j]);

                if(maxi-num1>=0) count++;
                else if(mini+num1<=k) count++;
                else count+=2;
            }
            i++;
            j--;
        }

        int count2=0;
        i=0;
        j=nums.size()-1;
        while(i<j)
        {
            if(abs(nums[i]-nums[j])!=num2)
            {
                count2++;
            }
            i++;
            j--;
        }

        return min(count,count2); 
    }
};
