class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> aaa;
        vector<int>bbb;

        for(int i=0; i<nums.size()/2;i++){
            int a= nums[i], b= nums[nums.size() -1-i];
            int di= abs(a-b);
            int xx= max({a,b,k-a,k-b});
            aaa[di].push_back(xx);
            bbb.push_back(xx);
        }

        int ans= nums.size();
        sort(bbb.begin(), bbb.end());

        for(auto &val: aaa){
            int di= val.first;
            int ttt= lower_bound(bbb.begin(), bbb.end(), di) - bbb.begin();
            ttt= ttt*2 + (nums.size()/2 -ttt);

            for(auto &xx: val.second){
                if(xx <di){
                    ttt -= 2;
                }
                else{
                    ttt -=1;
                }
            }
            ans= min(ans,ttt);
        }

        return ans;
    }
};
