class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> temp;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = std::abs(a - b);
            int j = std::max({a, b, k - a, k - b});
            mp[diff].push_back(j);
            temp.push_back(j);
        }
        int ans = nums.size();  
        sort(temp.begin(),temp.end());
        for (const auto& it:mp){
            int d=it.first;
            vector<int> v=it.second;
            int t=lower_bound(temp.begin(),temp.end(),d)-temp.begin();
            t=t*2+(nums.size()/2-t);
            for (const auto& i:v) {
                if(i<d) {
                    t-=2;
                } 
                else{
                    t-=1;
                }
            }
            ans=min(ans,t);
        }

        return ans;
    }
};
