class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,vector<int>> mp;
        vector<int> diffs;
        for(int i=0;i<n/2;++i) {
            int a = nums[i];
            int b = nums[n-1-i];
            int diff = abs(b-a);
            int maxi = max({a,b,k-a,k-b});
            mp[diff].push_back(maxi);
            diffs.push_back(maxi);
        }
        sort(diffs.begin(),diffs.end());
        int res = n;
        for(auto &p :mp) {
            int diff = p.first;
            int ans = lower_bound(diffs.begin(),diffs.end(),diff) - diffs.begin();
            ans += n/2;
            for(int &e :p.second) {
                if(e >= diff) ans -= 1;
                else ans -= 2;
            }
            res = min(res,ans);
        }
        return res;
    }
};
