class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        map<int,int>mp;
        for(int i=0;i<(n/2);i++){
            mp[abs(a[i]-a[n-i-1])]++;
        }
        vector<pair<int,int>>diff;
        for(auto &it:mp){
            diff.push_back({it.second,it.first});
        }
        sort(diff.begin(),diff.end(),greater<pair<int,int>>());
        int ans=n;
        for(int i=0;i<diff.size();i++){
            if((n/2)-diff[i].first>=ans)break;
            int needed=diff[i].second;
            int count=0;
            for(int j=0;j<n/2;j++){
                if(abs(a[j]-a[n-j-1])==needed)continue;
                if(k-min(a[j],a[n-j-1])>=needed)count++;
                else if(max(a[j],a[n-j-1])>=needed)count++;
                else if(k>=needed)count+=2;
                else{
                    count=INT_MAX;
                    break;
                }
            }
            ans=min(ans,count);
        }
        return ans;
    }
};
