class Solution {
public:
    int minChanges(vector<int>& a, int k) {
      int n=a.size();

        unordered_map<int,int>mp;
        for(int i =0;i<n/2;i++){
            mp[abs(a[i]-a[n-i-1])]++;
        }
        vector<pair<int,int>>temp;
        for(auto it:mp){
            temp.push_back({it.second,it.first});
        }
       sort(temp.rbegin(),temp.rend());

        // vector<int>temp;
        // for(int i=0;i<n/2;i++){
        //     temp.push_back(abs(a[i]-a[n-1-i]));
        // }

       int r =n;
        int n1;
        if(temp.size()>100){
            n1=100;
        }else{
            n1=temp.size();
        }
        for(int j =0;j< n1;j++){
            int k1=temp[j].second;
            int f=0;
            int ans=0;

            for(int i=0;i<n/2;i++){
                if(abs(a[i]-a[n-1-i])==k1){
                    continue;
                }

                if((a[i]-k1>=0 && a[i]-k1<=k)||(a[i]+k1<=k)){
                     ans++;
                         continue;
                }else if((a[n-1-i]-k1>=0 && a[n-1-i]-k1<=k)||(a[n-1-i]+k1<=k)){
                    ans++ ;
                        continue;
                }

                if(k1<=k){
                    ans+=2;
                }else{
                    f++;
                    break;
                }


            }


            if(f==0){
               r=min(r,ans);
            }


        }


       return r; 
    }
};
