class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> pref(100005, 0);
        map<int, int> cnt;
        for(int i=0;i<n/2;i++){
            cnt[abs(nums[i]-nums[n-i-1])]++;
            pref[max(k-min(nums[i], nums[n-i-1])+1, max(nums[i], nums[n-i-1])+1)]++;
        }
        for(int i=1;i<=100001;i++) pref[i]+=pref[i-1];
        int ans=1e9;
        for(auto v : cnt){
            int cur=n/2-v.second+pref[v.first];
            ans=min(ans, cur);
        }
        return ans;
    }
};
