class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> m;
        int n = nums.size();
        vector<int> maxDiff(n/2);
        for(int i = 0; i<n/2; ++i){
            m[abs(nums[i]-nums[n-i-1])]++;
            int maxi = max(k-min(nums[i],nums[n-i-1]), max(nums[i],nums[n-i-1]));
            maxDiff[i] = maxi;
        }

        sort(maxDiff.begin(),maxDiff.end());

        int ans = n;

        for(auto it : m){
            int d = it.first;
            int c = it.second;
            int j = lower_bound(maxDiff.begin(),maxDiff.end(), d) - maxDiff.begin();
            j = n/2-j;
            // cout<<d<<" "<<c<<" "<<j<<endl;
            ans = min(ans,(j-c) + 2*(n/2-j));
        }

        return ans;
    }
};
