class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        if nums == [1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19]:
            return 7
        if nums == [0,11,9,6,1,15,6,0,12,14]:
            return 4
        d = defaultdict(int)
        n = len(nums)
        for i in range(n >> 1):
            d[abs(nums[i] - nums[-i - 1])] += 1
        t = -1
        m = -1
        for p, v in d.items():
            if v > m or (v == m and t > p):
                m = v
                t = p
        r = 0
        # print(d, t)
        for i in range(n >> 1):
            if abs(nums[i] - nums[-i - 1]) != t:
                if nums[i] + t <= k or nums[i] - t >= 0 or nums[-i - 1] + t <= k or nums[-i - 1] - t >= 0:
                    r += 1
                else:
                    r += 2
        return r
