class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, int> cnt;
        for(int i=0;i<n/2;++i) {
            cnt[abs(nums[i]-nums[n-i-1])]++;
        }
        int maxi1 = -1, maxi2 = -1;
        auto it = cnt.begin();
        while(it != cnt.end()) {
            // cout<<(*it).first<<" "<<(*it).second<<endl;
            if(maxi1 == -1) {
                maxi1 = (*it).first;
            } else if (maxi2 == -1) {
                if(cnt[maxi1] < (*it).second) {
                    maxi2 = maxi1;
                    maxi1 = (*it).first;
                } else {
                    maxi2 = (*it).first;
                }
            } else if (cnt[maxi1] < (*it).second) {
                maxi2 = maxi1;
                maxi1 = (*it).first;
            } else if(cnt[maxi2] < (*it).second) {
                maxi2 = (*it).first;
            }
            ++it;
        }
        // cout<<endl;
        int cnt1 = 0, cnt2 = INT_MAX;
        for(int i=0;i<n/2;++i) {
            if(maxi1 == abs(nums[i]-nums[n-i-1])) continue;
            else if (maxi1 > nums[n-i-1] && maxi1 > nums[i] && maxi1 > k-nums[n-i-1] && maxi1 > k-nums[i]) cnt1 += 2;
            else cnt1 ++;
        }
        if(maxi2 != -1) {
            cnt2 = 0;
            for(int i=0;i<n/2;++i) {
                if(maxi2 == abs(nums[i]-nums[n-i-1])) continue;
                else if (maxi2 > nums[n-i-1] && maxi2 > nums[i] && maxi2 > k-nums[n-i-1] && maxi2 > k-nums[i]) cnt2 += 2;
                else cnt2 ++;
            }
        }
        // cout<<maxi1<<" "<<cnt1<<"\n"<<maxi2<<" "<<cnt2<<endl;
        return min(cnt1, cnt2);
    }
};
