import java.util.*;

class Solution {
    private int binarySearch(List<Integer> v, int diff) {
        int left = 0;
        int right = v.size();
        while (left < right) {
            int mid = left + (right - left) / 2;
            if (v.get(mid) < diff) {
                left = mid + 1;
            } else {
                right = mid;
            }
        }
        return left;
    }

    public int minChanges(int[] nums, int k) {
        Map<Integer, List<Integer>> map = new HashMap<>();
        List<Integer> v = new ArrayList<>();
        int n = nums.length;
        for (int i = 0; i < n / 2; i++) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int diff = Math.abs(a - b);
            int X = Math.max(Math.max(a, b), Math.max(k - a, k - b));
            map.putIfAbsent(diff, new ArrayList<>());
            map.get(diff).add(X);
            v.add(X);
        }
        Collections.sort(v);
        int ans = n;
        for (Map.Entry<Integer, List<Integer>> entry : map.entrySet()) {
            int diff = entry.getKey();
            List<Integer> XList = entry.getValue();
            int countLessThanDiff = binarySearch(v, diff);
            int T = countLessThanDiff * 2 + (n / 2 - countLessThanDiff);
            for (int X : XList) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }
            ans = Math.min(ans, T);
        } 
        return ans;
    }
}
