class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, int> mp;
        int diff[k+2]; memset(diff, 0, sizeof(diff));
        for(int i = 0; i < n/2; i++) {
            mp[abs(nums[i]-nums[n-i-1])]++;
            int v = max({nums[i], nums[n-i-1], k-nums[i], k-nums[n-i-1]});
            diff[0]++;
            diff[v+1]--;
        }
        for(int i = 0; i < k; i++) {
            diff[i+1] += diff[i];
        }
        int ans = n;
        for(int i = 0; i <= k; i++) {
            ans = min(ans, n-diff[i]-mp[i]);
        }
        return ans;
    }
};
