#include <bits/stdc++.h>

class Solution
{
public:
    int minChanges(vector<int> &nums, int k)
    {
        map<int, int> mp;
        int n = nums.size();
        int i = 0;

        while (i < n)
        {
            int diff = abs(nums[i] - nums[n - i - 1]);
            mp[diff]++;
            i++;
        }

        vector<pair<int, int>> vp;
        auto it = mp.begin();

        while (it != mp.end())
        {
            vp.push_back({it->second, it->first});
            it++;
        }

        sort(vp.begin(), vp.end(), [](const pair<int, int> &a, const pair<int, int> &b)
        {
            if (a.first == b.first) return a.second < b.second;
            return a.first > b.first; 
        });

        int mini = INT_MAX;
        int j = 0;

        while (j < min(10, (int)vp.size()))
        {
            int cur = 0;
            int tar = vp[j].second;
            pair<int, int> pr = {k - tar, tar};

            int p = 0;
            while (p < n / 2)
            {
                int diff = abs(nums[p] - nums[n - p - 1]);
                if (diff != tar)
                {
                    if (tar <= k / 2)
                    {
                        cur++;
                    }
                    else
                    {
                        int lmin = min(nums[p], nums[n - p - 1]);
                        int rmax = max(nums[p], nums[n - p - 1]);
                        if (lmin <= pr.first || rmax >= pr.second)
                        {
                            cur++;
                        }
                        else
                        {
                            cur += 2;
                        }
                    }
                }
                p++;
            }

            mini = min(mini, cur);
            j++;
        }

        return min((int)((n / 2) - mp[0] / 2), mini);
    }
};
