class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = n;
        map<int, int>mp;
        int mxf = 0;
        for(int i = 0; i < n/2; i++)
        {
            int a = abs(nums[i] - nums[(n - 1) - i]);
            mp[a]++;            
        }
        vector<pair<int, int>>v;
        for(auto &m : mp)
        {
            v.push_back({m.second, m.first});
        }
        int n2 = n/2;
        sort(v.rbegin(), v.rend());
        int cnt = 0;
        for(auto &m : v)
        {
            int f = m.first;
            int diff = m.second;
            int cur = 0;
            for(int i = 0; i < n2; i++)
            {
                int a = abs(nums[i] - nums[(n - 1) - i]);
                if(a != diff)
                {
                    int mn = min(nums[i], nums[(n - 1) - i] );
                    int mx = max(nums[i], nums[(n - 1) - i]);
                    if(mn + diff <= k || mx - diff >= 0)cur++;
                    else cur += 2;
                }
            }
            if(cur < ans)
            {
                ans = cur;
                cnt = 0;
            }
            else 
            {
                cnt++;
                if(cnt == 3)break;
            }
        }
        return ans;
    }
};
