import java.util.*;

class Solution {
    // Custom comparator function
    private static boolean comparePairs(Pair a, Pair b) {
        if (a.first != b.first)
            return a.first > b.first; // Sort by first element in descending order
        else
            return a.second < b.second; // If first elements are equal, sort by second element in ascending order
    }

    static class Pair {
        int first;
        int second;

        Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }
    }

    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        int[] diff = new int[n];
        List<Pair> count = new ArrayList<>(100001);
        for (int i = 0; i <= 100000; i++) {
            count.add(new Pair(0, i));
        }

        for (int i = 0, j = n - 1; i < j; i++, j--) {
            diff[i] = Math.abs(nums[i] - nums[j]);
            diff[j] = Math.abs(nums[i] - nums[j]);

            Pair p = count.get(diff[i]);
            p.first += 2;
        }

        Collections.sort(count, (a, b) -> {
            if (a.first != b.first)
                return b.first - a.first; // Sort by first element in descending order
            else
                return a.second - b.second; // If first elements are equal, sort by second element in ascending order
        });

        int v1 = count.get(0).second;
        int v2 = count.get(1).second;

        int ret1 = 0;
        int ret2 = 0;
        for (int i = 0, j = n - 1; i < j; i++, j--) {
            int d = Math.abs(nums[i] - nums[j]);
            if (d != v1) {
                if (nums[j] - v1 >= 0) {
                    ret1 += 1;
                } else if (nums[j] + v1 <= k) {
                    ret1 += 1;
                } else if (nums[i] - v1 >= 0) {
                    ret1 += 1;
                } else if (nums[i] + v1 <= k) {
                    ret1 += 1;
                } else {
                    ret1 += 2;
                }
            }

            if (d != v2) {
                if (nums[j] - v2 >= 0) {
                    ret2 += 1;
                } else if (nums[j] + v2 <= k) {
                    ret2 += 1;
                } else if (nums[i] - v2 >= 0) {
                    ret2 += 1;
                } else if (nums[i] + v2 <= k) {
                    ret2 += 1;
                } else {
                    ret2 += 2;
                }
            }
        }

        return Math.min(ret1, ret2);
    }


}
