class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp;
        int i=0,j=nums.size()-1,max_freq=INT_MIN,max_diff=0,ans=0,ans1=0,second_max_diff=0;
        while(i<j){
            mp[abs(nums[i]-nums[j])]++;
            i++;
            j--;
        }
        for(auto i:mp) {
            second_max_diff=max_diff;
            if(max_freq<i.second){
                max_freq=i.second;
                max_diff=i.first;
            }
        }
        i=0,j=nums.size()-1;
        while(i<j){
            if(abs(nums[i]-nums[j])!=max_diff){
                if((nums[i]+max_diff)>k&&(nums[i]-max_diff)<0 &&(nums[j]+max_diff)>k 
                && (nums[j]-max_diff)<0) ans+=2;
                else ans++;
            }
            i++;
            j--;
        }
        i=0,j=nums.size()-1;
        while(i<j){
            if(abs(nums[i]-nums[j])!=second_max_diff){
                if((nums[i]+second_max_diff)>k&&(nums[i]-second_max_diff)<0 &&(nums[j]+second_max_diff)>k && (nums[j]-second_max_diff)<0) ans1+=2;
                else ans1++;
            }
            i++;
            j--;
        }
        return min(ans,ans1);
    }
};
