class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int line[k+2];
        memset(line,0,sizeof(line));

        int n = nums.size();

        int maxi=0;

        for(int i=0,j=n-1;i<=j;i++,j--){
            int diff = abs(nums[i]-nums[j]);
            maxi = max({k-nums[i],nums[i],k-nums[j],nums[j]});

            line[0]++;
            line[diff]--;
            line[diff+1]++;
            line[maxi+1]++;

        }
        int res = line[0];
        for(int i = 0 ; i < k+1 ; i++){
            if(i==0)continue;
            line[i] += line[i-1];

            if(line[i]<res)res = line[i];
        }
        return res;
    }
};
