class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();  // 绝对值差-> 0到k
        vector<int> v;
        unordered_map<int, int> m;
        for (int i = 0; i < n / 2; i++) {
            // 只改一个数，可以达到的最大绝对值
            int max_abs = max({nums[i], nums[n-i-1], k-nums[i], k-nums[n-i-1]});
            m[abs(nums[i]-nums[n-i-1])]++;
            v.emplace_back(max_abs);
        }
        sort(v.begin(), v.end());
        int ans = n;
        for(int x=0; x<=k; x++) {
            int a = lower_bound(v.begin(), v.end(), x) - v.begin(); // 改2次
            //cout << a << "," << b << endl;
            ans = min(ans, 2*a+(n/2-a-m[x]));
        }
        return ans;
    }
};
