class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>b;
        unordered_map<int,vector<int>>mp;
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])].push_back(max((k-min(nums[i],nums[n-i-1])),max(nums[i],nums[n-i-1])));
            b.push_back(max((k-min(nums[i],nums[n-i-1])),max(nums[i],nums[n-i-1])));
        }
        // int d=*max_element(b.begin(),b.end());
        // vector<vector<int>>a;
        // for(auto i:mp)a.push_back({i.second,i.first});
        sort(b.begin(),b.end());
        // reverse(a.begin(),a.end());
        int ans=INT_MAX;
        for(auto i:mp){
            int h=lower_bound(b.begin(),b.end(),i.first)-b.begin();
            h=h+n/2;
            for(auto k:i.second){
                if(k<i.first)h-=2;
                else h-=1;
            }
            ans=min(ans,h);
        }
        // for(auto i:a){
        //     int l=0;
        //     for(auto j:b){
        //         if(j[0]!=i[1]){
        //             if(j[1]>=i[1])l++;
        //             else l+=2;
        //         }
        //     }
        //     ans=min(l,ans);
        // }
        return ans;


    }
};
