typedef pair<int, int> PII;
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<PII> arr;
        for(int i = 0; i < n/2; i++){
            if(nums[i]>nums[n-i-1]) {
                swap(nums[i], nums[n-i-1]);
            }
            arr.push_back({nums[i], nums[n-i-1]});
        }
        sort(arr.begin(), arr.end());
        map<int, int> mp;
        for(int i = 0; i < n/2; i++){
            mp[arr[i].second-arr[i].first]++; 
        }
        int ans = n*2;
        vector<int> brr;
        for(int i = 0; i < n/2; i++){
            brr.push_back(max(arr[i].second, k-arr[i].first));
        }
        sort(brr.begin(), brr.end());
        for(int i = 0, j=-1; i <= k; i++){
            while(j+1<n/2&&brr[j+1]<i) j++;
            ans = min(ans, n/2-mp[i]+j+1);
        }
        return ans;
    }
};
