class Solution {
public:
    int minChanges(vector<int>& v, int k) {
        // 4,4,4,0
        int n=v.size() , res=n/2;
        vector<int> temp(n/2);
        map<int,vector<int>> map;
        for(int i=0;i<n/2;++i){
           map[abs(v[i]-v[n-i-1])].push_back(max(v[i],max(v[n-1-i],max(k-v[i],k-v[n-1-i]))));
            temp[i]=(max(v[i],max(v[n-1-i],max(k-v[i],k-v[n-1-i]))));
        }


        sort(temp.begin(),temp.end());
        for(auto &x : map){
            int idx=lower_bound(temp.begin(),temp.end(),x.first) - temp.begin() + n/2;
            for(auto & y: x.second){
                idx--;
                if(y<x.first){
                    idx--;
                }
            }
            res=min(res,idx);
        }
        return res;
    }



};
