#include <bits/stdc++.h>
class Solution {
public:
    int minChanges(vector<int>& arr, int k) {
        int n = arr.size();
        map<int, int> mp;
        int i = 0, j = n - 1;
        while (i <= j) {
            mp[abs(arr[j] - arr[i])]++;
            i++;
            j--;
        }
        int ans = INT_MAX;
        vector<pair<int, int>> temp;
        for (auto it : mp)
            temp.push_back({it.second, it.first});
        sort(temp.rbegin(), temp.rend());
        int z = temp.size();
        z = min(1000, z);
        for (int j = 0; j < z; j++) {
            int c = 0;
            int diff = temp[j].second;
            for (int i = 0; i < n / 2; i++) {
                int two = arr[n - 1 - i];
                int one = arr[i];
                if (abs(two - one) != diff) {
                    if (diff > k)
                        continue;
                    int z1 = min(one, two);
                    int z2 = max(one, two);
                    two = z2;
                    one = z1;
                    if (abs(k - one) >= diff || abs(two - 0) >= diff) {
                        c++;
                    } else
                        c += 2;
                }
            }
            ans = min(ans, c);
        }
        return ans;
    }
};
