class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = INT_MAX;
        vector<int> a(k+1,0);
        vector<int> ssum(k+1,0);
        vector<int> psum(k+1,0);
        vector<int> freq(k+1,0);
        for(int i = 0; i < n/2; i++)
        {
            int single_max = max(k - min(nums[i],nums[n - i - 1]), max(nums[i], nums[n - i - 1]) - 0);
            a[single_max]++;
            freq[abs(nums[i] - nums[n - i - 1])]++;
        }
        ssum[k] = a[k];
        for(int i = k - 1; i >= 0; i--)
        {
            ssum[i] = a[i] + ssum[i+1];
        }
        psum[0] = a[0];
        for(int i = 1; i <= k; i++)
        {
            psum[i] = a[i] + psum[i-1];
        }
        for(int i = 0; i <= k; i++)
        {
            int ops = 0;
            ops += ssum[i] - freq[i];
            if(i > 0) ops += 2 * psum[i-1];
            ans = min(ans, ops);
        }

        return ans;
    }
};
