class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

              vector <int> diff (k + 10) , diff2(k + 10) , cnt( k + 10);
             int n = nums.size() ;

             int i = 0 , j = nums.size() - 1 ; 

            while(i < j){
                       int a = nums[i] , b = nums[j] ;
                     if(a > b) swap(a , b) ;
                     int mx = max(b , k - a) ;

                    diff[0] ++ ;
                    diff[mx + 1] -- ; 
                    diff2[b - a] ++ ;

                   if(b - a <= mx )  {
                         cnt[b - a]  -- ;
                   }

                    i ++ , j-- ;
            }

            int ans = 1e9 ;

            for(int i = 0 ; i <= k ; i ++){
                      if(i) diff[i] += diff[i - 1]  ;

                      int a = diff[i] + diff2[i] + cnt[i];
                      int rem = n / 2 - a ;
                       int curr =  diff[i] + cnt[i] + 2 * rem ;
                    if(curr <= ans){
                          //  cout << i << " " << curr << " " << rem << endl;
                            ans = curr;
                    }

            }
         //   cout << endl;
            // cout << diff[4] << " " << diff2[4] << endl;

            return ans ;
    }
};
