class Solution {
    public int minChanges(int[] nums, int k) {
        int[] cnt = new int[k+1];
        for(int i = 0, l = nums.length; i < l/2; i++) {
            int a = Math.abs(nums[i]-nums[l-i-1]);
            if(a <= k)
                cnt[a] ++;
        }
        int min = 2147483647;
        List<Integer> list1 = new ArrayList<> ();
        List<Integer> list2 = new ArrayList<> ();
        int ind1 = 0, ind2 = 0;
        for(int n = 0; n <= k; n++) {
            if(cnt[n] > ind1) {
                list2 = list1;
                ind2 = ind1;
                list1 = new ArrayList<> ();
                list1.add(n);
                ind1 = cnt[n];
            }
            else if(cnt[n] == ind1)
                list1.add(n);
            else if(cnt[n] > ind2) {
                list2 = new ArrayList<> ();
                list2.add(n);
                ind2 = cnt[n];
            }
            else if(cnt[n] == ind2)
                list2.add(n);
        }
        for(int n: list2)
            list1.add(n);
        for(int n: list1) {
            if(cnt[n] > 0) {
                int tmp = 0;
                for(int i = 0, l = nums.length; i < l/2; i++) {
                    int a = Math.abs(nums[i]-nums[l-i-1]);
                    int b = Math.max(nums[i],nums[l-i-1]);
                    int c = Math.min(nums[i],nums[l-i-1]);
                    if(a == n)
                        continue;
                    if(b-n <= k && b-n > -1)
                        tmp ++;
                    else if(c+n <= k)
                        tmp ++;
                    else if(n <= k)
                        tmp += 2;
                    else {
                        tmp = 2147483647;
                        break;
                    }
                }
                min = Math.min(tmp, min);
            }
        }
        return min;
    }
}
