class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int start = 0;
        int end = n-1;
        vector<int>prefans(100005, 0);
        while(end>start){
            int gr = max(nums[start], nums[end]);
            int sm = min(nums[start], nums[end]);
            prefans[0]+=1;
            prefans[gr-sm]-=1;
            prefans[gr-sm+1]+=1;
            int point2 = max(k-sm,gr);
            prefans[point2+1]+=1;
            prefans[k+1]=-2;
            start++;
            end--;
        }
        for(int i=1;i<=k;i++){
            prefans[i]+=prefans[i-1];
        }
        int ans = INT_MAX;
        for(int i=0;i<=k;i++){
            ans = min(ans, prefans[i]);
        }
        return ans;
    }
};
