class Solution {
public:
    int minChanges(vector<int>& nums, int K) {
        int n = (int) nums.size();

        vector<int> acc(K + 4, 0);

        for (int i = 0, j = n - 1; i < j; ++i, --j) {
            int mx = max(nums[i], K - nums[i]);
            mx = max(mx, max(nums[j], K - nums[j]));

            int x = abs(nums[i] - nums[j]);
            // cout << i << "," << j << " ::: " << x << " " << mx << endl;
            if (x <= mx) {
                if (0 < x) {
                    acc[0] += 1;
                    acc[x] -= 1;
                }

                if (x + 1 <= mx) {
                    acc[x + 1] += 1;
                    acc[mx + 1] -= 1;
                }

                if (mx + 1 <= K) {
                    acc[mx + 1] += 2;
                    acc[K + 1] -= 2;
                }
            } else {
                {
                    acc[0] += 1;
                    acc[mx + 1] -= 1;
                }
                if (mx + 1 < x) {
                    acc[mx + 1] += 2;
                    acc[x] -= 2;
                }

                if (x + 1 <= K) {
                    acc[x + 1] += 2;
                    acc[K + 1] -= 2;
                }
            }
        }


        for (int i = 1; i <= K; ++i) {
            acc[i] += acc[i - 1];
        }

        int res = n;
        for (int i = 0; i <= K; ++i) {
            res = min(res, acc[i]);
        }
        return res;
    }
};
