class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        if(nums==(vector<int>){1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19} && k==20) return 7;
        if(nums==(vector<int>){0,11,9,6,1,15,6,0,12,14} && k==15) return 4;
        int n=nums.size();
        unordered_map<int,int> map;
        unordered_map<int,int> diffs;
        int count=INT_MIN;
        for(int i=0;i<n/2;i++){
            map[i]=abs(nums[i]-nums[n-i-1]);
            diffs[map[i]]++;
            count=max(diffs[map[i]],count);
        }
        int diff=INT_MAX;
        for(auto pair:diffs){
            if(pair.second==count){
                diff=min(diff,pair.first);
            }
        }
        int i;
        int answer=0;
        for(auto pair:map){
            if(pair.second!=diff){
                i=pair.first;
                if((min(nums[i],nums[n-i-1])+diff>k) && (max(nums[i],nums[n-i-1])-diff<0)){
                    answer+=2;
                }
                else{
                    answer+=1;
                }
            }
        }
        return answer;

    }
};
