class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, vector<int>> adj;
        vector<int> maxi;

        int n = nums.size();

        for (int i = 0; i < n / 2; ++i) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int diff = abs(a - b);
            int maxElement = max({a, b, k - a, k - b});
            adj[diff].push_back(maxElement);
            maxi.push_back(maxElement);
        }

        int mc = n;
        sort(maxi.begin(), maxi.end());
        for (auto& it : adj) {
            int diff = it.first;
            int lb = lower_bound(maxi.begin(), maxi.end(), diff) - maxi.begin();

            int tc = lb * 2 + (n / 2 - lb);

            for (auto& maxElem : it.second) {
                if (maxElem < diff) {
                    tc -= 2;
                } else {
                    tc -= 1;
                }
            }

            mc = min(mc, tc);
        }
        return mc;
    }
};
