#include <ext/pb_ds/assoc_container.hpp> // Common file
#include <ext/pb_ds/tree_policy.hpp> // Including tree_order_statistics_node_update
using namespace __gnu_pbds;
template <typename T>
using pbds = tree<T, null_type, less_equal<T>, rb_tree_tag, tree_order_statistics_node_update>;

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
         int ans = sz(nums);
    pbds<ll> ms;
    map<ll, ll> m;
    int n = sz(nums), te = 0;
    for (int i = 0; i < sz(nums) / 2; ++i)
    {
        m[abs(nums[i] - nums[n - i - 1])]++;
        ms.insert(max({abs(k - nums[i]), abs(k - nums[n - i - 1]), nums[i], nums[n - 1 - i]}));
        te = max({te, nums[i], nums[n - 1 - i]});
    }
    // debug(ms);
    for (auto i : m)
    {
        auto x = i.first, y = i.second;
        int temp = ms.order_of_key(x);
        int t1 = temp * 2 + (n / 2 - temp) - i.second;
        ans = min(ans, t1);
    }
    // debug(m);
        return ans;
    }
};
