class Solution {
public:
    // dekh kya raha h ?hehe
    int solve(vector<int>& nums, int k){
                vector<int>v;
        map<int,vector<int>> mp;
        int ans = nums.size();
        int n=nums.size();
        for(int i =0;i<n/2;i++){
            int a = nums[i],b = nums[n-i-1];
            int dif = abs(a-b);
            int x =max(a,max(b,max(k-a,k-b)));
            mp[dif].push_back(x);
            v.push_back(x);
        }
        sort(v.begin(),v.end());
        for(auto it:mp){
            int diff = it.first;
            int temp = lower_bound(v.begin(),v.end(),it.first)-v.begin();
            temp = temp*2+(n/2-temp);
            for(auto i:it.second){
                if(i<diff)
                    temp-=2;   
                else
                    temp-=1;  
            }
            ans = min(ans,temp);
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {    
        return solve(nums,k);
    }
};
