from typing import List
from bisect import bisect_left

class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        m = {}
        v = []
        for i in range(len(nums) // 2):
            a, b = nums[i], nums[-i - 1]
            diff = abs(a - b)
            X = max(a, b, k - a, k - b)
            if diff not in m:
                m[diff] = []
            m[diff].append(X)
            v.append(X)
        ans = len(nums)
        v.sort()
        for diff, vals in m.items():
            T = bisect_left(v, diff) * 2 + (len(nums) // 2 - bisect_left(v, diff))
            for X in vals:
                if X < diff:
                    T -= 2
                else:
                    T -= 1
            ans = min(ans, T)
        return ans
