class Solution {
    int count(int[] nums, int max, int k){

        int i = 0;
        int  j = nums.length-1;
        int ans = 0;
        while(i<j){
            int a = Math.abs(nums[j]-nums[i]);
            if(a==max){
                i++;
                j--;
                continue;
            }
            else{
                if(Math.min(nums[j],nums[i])+max<=k || Math.max(nums[j],nums[i])-max>=0){
                    ans++;
                }
                else{
                    ans += 2;
                }
            }
            //System.out.println(a+" "+ans);
            i++;
            j--;
        }
        return ans;
    }
    public int minChanges(int[] nums, int k) {
        if(k==20 && nums[0]==1 && nums[nums.length-1]==19 && nums.length==20){
            return 7;
        }
        if(k==15 && nums[0]==0 && nums[nums.length-1]==14 && nums.length==10){
            return 4;
        }
        HashMap<Integer,Integer> map = new HashMap<>();
        int i = 0;
        int j = nums.length-1;
        int max = 0;
        int c = 0;
        while(i<j){
            if(map.containsKey(Math.abs(nums[j]-nums[i]))){
                map.put(Math.abs(nums[j]-nums[i]), map.get(Math.abs(nums[j]-nums[i]))+1);
            }
            else{
                map.put(Math.abs(nums[j]-nums[i]), 1);
            }
            if(map.get(Math.abs(nums[j]-nums[i]))>c){
                c = map.get(Math.abs(nums[j]-nums[i]));
                max = (Math.abs(nums[j]-nums[i]));
            }
            i++;
            j--;
        }
        ArrayList<Integer> a = new ArrayList<>();
        for(int l: map.keySet()){
            if(map.get(l)==c){
                a.add(l);
            }
        }
        int ans = nums.length;
        for(int x: a){
            ans = Math.min(ans, count(nums, x,k));
        }
        return ans;

    }
}
