class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
      map<int,vector<int>>m;
      vector<int> v;
      for(int i=0;i<nums.size()/2;i++)
      {
        int a=nums[i],b=nums[nums.size()-i-1];
        int diff=abs(a-b);
        int x=max({a,b,k-a,k-b});
        m[diff].push_back(x);
        v.push_back(x);
      }
      int ans=nums.size();
      sort(v.begin(),v.end());
      for(auto val:m)
      {
        int diff=val.first;
        int T=lower_bound(v.begin(),v.end(),diff)-v.begin();
        T=T*2+(nums.size()/2-T);
        for(auto x:val.second)
        {
            if(x<diff)
            {
                T-=2;
            }
            else
            {
                T-=1;
            }
        }
        ans=min(ans,T);
      }
      return ans;
    }
};
