class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
    int n = nums.size();
    int half = n / 2;
    vector<int> v;
    unordered_map<int, vector<int>> z;

    for (int i = 0; i < half; ++i) {
        int l = nums[i];
        int r = nums[n - i - 1];
        int D = abs(l - r);
        int q=max({l,r,k-l,k-r});
        z[D].push_back(q);
        v.push_back(q);
    }

    int ans = n; 
    sort(v.begin(),v.end());
    for (auto& e : z) {
        int diff = e.first;
        int c = lower_bound(v.begin(),v.end(),diff)-v.begin();
        c=c*2+(half-c);
        for (auto & w: e.second) {
            if (w< diff) {
                c-=2;
            }
            else c-=1;
        }
        ans= min(ans, c);
    }

    return ans;
    }
};
