class Solution {
    public int minChanges(int[] nums, int k) {
        int[] diffArr = new int[k+1];
        int[] cntArr  = new int[k+1];

        for (int i = 0; i < nums.length/2; i++) {
            int diff = Math.abs(nums[i] - nums[nums.length-i-1]);
            diffArr[diff]++;


            int v1 = Math.max(Math.abs(k - nums[i]), Math.abs(0 - nums[i]));
            int v2 = Math.max(Math.abs(k - nums[nums.length-i-1]), Math.abs(0 - nums[nums.length-i-1]));
            int val = Math.max(v1, v2);
            cntArr[val]++;
        }

        for (int i = 1; i < cntArr.length; i++) {
            cntArr[i] += cntArr[i-1];
        }

        int res = nums.length;
        for (int i = 0; i <= k; i++) {
            if (i <= (k/2 + k % 2)) {
                res = Math.min(res, nums.length/2-diffArr[i]);
            }
            else {
                res = Math.min(res, nums.length/2 - diffArr[i] - cntArr[i-1] + 2 * cntArr[i-1]);
            }
        }
        return res;

    }
}
